<template>
  <v-expand-transition>
    <v-card class="mt-3" v-show="item">
      <v-card-title>
        <span class="font-weight-light">{{ competition.comName }}</span>
        <v-spacer />
        <span class="font-weight-light">{{ competition.department }}</span>
      </v-card-title>

      <v-card-subtitle>
        <v-list-item>
          <v-list-item-content>
            <v-list-item-title v-if="item.opusName">
              作品名称：{{ item.opusName }}
            </v-list-item-title>
            <v-list-item-title>
              指导老师：{{ item.adviserName }}
            </v-list-item-title>
            <v-list-item-title>
              参赛成员：{{ item.students | TeamMemberTextFilter }}
            </v-list-item-title>
            <v-list-item-title
              >比赛状态：{{ competition.currentStageName || "已结束" }}
            </v-list-item-title>
            <v-list-item-title>
              参赛情况：{{ teamStageText }}
            </v-list-item-title>
            <v-list-item-title>
              获奖情况：{{ item.message || "无" }}
            </v-list-item-title>
          </v-list-item-content>
        </v-list-item>
      </v-card-subtitle>

      <v-card-actions>
        <v-btn text color="primary" @click="show = !show">
          比赛详情
        </v-btn>
        <confirm-dialog
          v-if="item.message"
          :title="imageUrl ? '' : '上传证书'"
          btn-text
          btn-color="success"
          max-width="600px"
          no-persistent
          >{{ imageUrl ? "查看证书" : "上传证书" }}
          <template #container>
            <v-img
              v-if="imageUrl"
              :src="imageUrl"
              lazy-src=""
            />
            <v-file-input
              v-else
              v-model="imageFile"
              show-size
              :loading="uploadLoading"
              :rules="[
                value =>
                  !value || value.size < 1048576 || '照片大小必须小于 1 MB!'
              ]"
              accept="image/png, image/jpeg, image/bmp"
              placeholder="选择一张图片"
              prepend-icon="mdi-camera"
              append-outer-icon="mdi-upload"
              @click:append-outer="imageUpload"
              label="证书图片"
            ></v-file-input>
          </template>
        </confirm-dialog>
      </v-card-actions>
      <v-expand-transition>
        <div v-show="show">
          <v-list-item>
            <v-list-item-content>
              <v-list-item-title
                >比赛等级：{{ competition.comLevel }}</v-list-item-title
              >
              <v-list-item-title
                >参赛范围：{{ competition.comCondition }}</v-list-item-title
              >
              <v-list-item-title>
                比赛阶段：
                <ul>
                  <li v-for="s in competition.competitionStages" :key="s.name">
                    <span>
                      {{ s.stageName }} : {{ s.startTime }} -
                      {{ s.endTime }}</span
                    >
                  </li>
                </ul>
              </v-list-item-title>
            </v-list-item-content>
          </v-list-item>
        </div>
      </v-expand-transition>
    </v-card>
  </v-expand-transition>
</template>

<script>
import ConfirmDialog from "@/components/ConfirmDialog";
import { upload } from "@/api/student";

export default {
  name: "PersonalCompetitionItem",
  components: { ConfirmDialog },
  filters: {
    TeamMemberTextFilter(value) {
      return value.map(e => e.stuName).join(",");
    }
  },
  props: {
    item: Object
  },
  data() {
    return {
      show: false,
      competition: this.item.competition,
      imageFile: null,
      uploadLoading: false,
      imageUrl: this.item.url
    };
  },
  computed: {
    teamStageText() {
      return (
        (this.item.competition.competitionStages &&
          this.item.competition.competitionStages[this.item.teamStage - 1] &&
          this.item.competition.competitionStages[this.item.teamStage - 1]
            .stageName) ||
        "获奖"
      );
    }
  },
  methods: {
    imageUpload() {
      this.uploadLoading = true;
      let file = new FormData(); //创建form对象
      file.append("file", this.imageFile); //通过append向form对象添加数据
      upload(this.item.id, file)
        .then(({ code, msg, data }) => {
          if (code !== 200) return;
          this.imageUrl = data;
          this.$message.$emit("message", { text: msg });
        })
        .finally(() => {
          this.uploadLoading = false;
        });
    }
  }
};
</script>

<style scoped></style>
